package Flow_Control;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num;
		num=Integer.parseInt(args[0]);
		if(num%2==0)
		{
			System.out.println("num is Even");
		}
		else
		{
			System.out.println("num is odd");
		}

	}

}
